// Initialize audio
const audio = new Audio('birthday-song.mp3');
audio.loop = true;

// Enhanced Event Listeners
document.addEventListener('DOMContentLoaded', () => {
    const prankMessage = document.querySelector('.prank-message');
    const revealBtn = document.getElementById('revealBtn');
    let hasClicked = false;

    prankMessage.addEventListener('click', function() {
        if (hasClicked) return;
        hasClicked = true;
        
        this.classList.add('jelly');
        
        setTimeout(() => {
            this.classList.remove('jelly');
            revealBtn.style.display = 'block';
            prankMessage.classList.add('shift-up');
            requestAnimationFrame(() => {
                revealBtn.classList.add('show');
            });
        }, 500);
    });

    document.addEventListener('visibilitychange', () => {
        if (document.hidden) {
            audio.pause();
        }
    });

    // Initialize Swiper
    new Swiper('.swiper', {
        loop: true,
        effect: 'coverflow',
        grabCursor: true,
        coverflowEffect: {
            rotate: 50,
            stretch: 0,
            depth: 100,
            modifier: 1,
            slideShadows: true,
        },
        pagination: {
            el: '.swiper-pagination',
            clickable: true
        },
        navigation: {
            nextEl: '.swiper-button-next',
            prevEl: '.swiper-button-prev',
        }
    });
});

// Enhanced reveal celebration
function revealCelebration() {
    const prankPage = document.getElementById('prankPage');
    const celebrationPage = document.getElementById('celebrationPage');
    
    prankPage.style.transform = 'scale(0.95)';
    prankPage.style.opacity = '0';
    
    setTimeout(() => {
        prankPage.style.visibility = 'hidden';
        celebrationPage.classList.add('show-celebration');
        audio.play().catch(console.error);

        // Add timer for chat notification
        setTimeout(showChatNotification, 5000);
    }, 800);
}

function showChatNotification() {
    const chatNotification = document.getElementById('chatNotification');
    chatNotification.classList.add('show');
    
    // Add sound effect
    const notificationSound = new Audio('data:audio/wav;base64,');
    notificationSound.volume = 0.3;
    notificationSound.play();
    
    // Add wiggle animation after showing
    setTimeout(() => {
        chatNotification.style.animation = 'bounce 1s ease infinite';
    }, 500);
}

function toggleMusic() {
    if (audio.paused) {
        audio.play();
    } else {
        audio.pause();
    }
}
